
function createXHR(){if(window.XMLHttpRequest){return new XMLHttpRequest();}
return new ActiveXObject("Microsoft.XMLHTTP");}
function ajax(obj){obj.type=obj.type||"get";obj.async=obj.async==undefined?true:obj.async;var xhr=createXHR();var paramStr=getParamStr(obj.data);if(obj.type.toLowerCase()=="get"){obj.url+=paramStr?("?"+paramStr):"";}
xhr.open(obj.type,obj.url,obj.async);if(obj.type.toLowerCase()=="get"){xhr.send(null);}
else{xhr.setRequestHeader("Content-type","application/x-www-form-urlencoded");xhr.send(paramStr);}
if(obj.async){xhr.onreadystatechange=function(){if(xhr.readyState==4){callback();}}}
else{callback();}
function callback(){if(xhr.status==200){if(obj.success)
obj.success(xhr.responseText);}
else{if(obj.error){obj.error();}}}}
function getParamStr(data){var arr=[];for(var key in data){var str=key+"="+data[key];arr.push(str);}
return arr.join("&");}