
function Bullet(){this.ele=document.createElement("div");this.id=parseInt(Math.random()*100000000);this.init=function(){gameEngine.allBullet[this.id]=this;gameEngine.ele.appendChild(this.ele);this.ele.className="bullet";this.ele.style.left=myPlane.ele.offsetLeft+myPlane.ele.offsetWidth/2-this.ele.offsetWidth/2+"px";this.ele.style.top=myPlane.ele.offsetTop-this.ele.offsetHeight+"px";return this;}
this.move=function(){let that=this;this.timer=setInterval(()=>{var y=that.ele.offsetTop-10;if(y<-18){clearInterval(that.timer);gameEngine.ele.removeChild(that.ele);delete gameEngine.allBullet[that.id];}
else{that.ele.style.top=y+"px";}},50);}
this.boom=function(){clearInterval(this.timer);this.ele.className="bullet-die";var[that,i]=[this,0];const dieImgs=["images2/die1.png","images2/die2.png"];var dieTimer=setInterval(()=>{if(i>=1){clearInterval(dieTimer);gameEngine.ele.removeChild(that.ele);}else{that.ele.style.background="url("+dieImgs[++i]+") no-repeat";}},100);}}